mapper.insert:
INSERT INTO TB_STORY
(
    CATEGORY
    , STORY_NM
    , TITLE
    , IMPORTANT_YN
    , VIEW_YN
    , VIEW_UNLIMIT_YN
    , VIEW_ST_DT
    , ORDER_NO
    , FILE_GRP_SEQ
    , IMG_GRP_SEQ
    , CONTENTS
    , DEL_YN
    , CRE_DT
    , CRE_ID
    , UPD_DT
    , UPD_ID
) VALUES (
    #{category}
    , #{storyNm}
    , #{title}
    , #{importantYn}
    , #{viewYn}
    , #{viewUnlimitYn}
    , #{viewStDt}
    , #{orderNo}
    , #{fileGrpSeq}
    , #{imgGrpSeq}
    , #{contents}
    , 'N'
    , now()
    , #{userSeq}
    , now()
    , #{userSeq}
)

mapper.update:
UPDATE
    TB_STORY
SET
    CATEGORY=#{category}
    , STORY_NM=#{storyNm}
    , TITLE=#{title}
    , IMPORTANT_YN=#{importantYn}
    , VIEW_YN=#{viewYn}
    , VIEW_UNLIMIT_YN=#{viewUnlimitYn}
    , VIEW_ST_DT=#{viewStDt}
    , ORDER_NO=#{orderNo}

        , FILE_GRP_SEQ=#{fileGrpSeq}


        , IMG_GRP_SEQ=#{imgGrpSeq}

    , CONTENTS=#{contents}
    , UPD_DT=now()
    , UPD_ID=#{userSeq}
WHERE
    STORY_SEQ=#{seq}

UPDATE TB_STORY SET ORDER_NO =#{order,jdbcType=VARCHAR}
, IMPORTANT_YN='N'
WHERE STORY_SEQ=#{seq}

UPDATE
    TB_FILE
SET
    FILE_GRP_SEQ =#{fgs}
WHERE
    FILE_GRP_SEQ=#{newFgs}
