mapper.select:
SELECT A.* , @rownum:=@rownum+1 no FROM ( SELECT s.STORY_SEQ , s.CATEGORY , s.STORY_NM , s.TITLE , s.IMPORTANT_YN , s.VIEW_UNLIMIT_YN , s.VIEW_YN , DATE_FORMAT(s.VIEW_ST_DT, '%Y.%m.%d') VIEW_ST_DT , DATE_FORMAT(s.VIEW_END_DT, '%Y.%m.%d') VIEW_END_DT FROM TB_STORY s WHERE IFNULL(s.DEL_YN, 'N') = 'N'
and s.TITLE LIKE concat('%',#{keyword},'%')
and s.STORY_NM LIKE concat('%',#{keyword},'%')
and s.CATEGORY = #{category}
ORDER BY s.CRE_DT DESC ) A, (SELECT @rownum:=0) rn ORDER BY no LIMIT ${startRow}, ${cntPerPage}

mapper.insert:
INSERT INTO TB_STORY
(
    CATEGORY
    , STORY_NM
    , TITLE
    , IMPORTANT_YN
    , VIEW_YN
    , VIEW_UNLIMIT_YN
    , VIEW_ST_DT
    , VIEW_END_DT
    , FILE_GRP_SEQ
    , CONTENTS
    , DEL_YN
    , CRE_DT
    , CRE_ID
) VALUES (
    #{category}
    , #{storyNm}
    , #{title}
    , #{importantYn}
    , #{viewYn}
    , #{viewUnlimitYn}
    , #{viewStDt}
    , #{viewEndDt}
    , #{fileGrpSeq}
    , #{contents}
    , 'N'
    , now()
    , #{creId}
)

mapper.update:
UPDATE
    TB_STORY
SET
    CATEGORY=#{category}
    , STORY_NM=#{storyNm}
    , TITLE=#{title}
    , IMPORTANT_YN=#{importantYn}
    , VIEW_YN=#{viewYn}
    , VIEW_UNLIMIT_YN=#{viewUnlimitYn}
    , VIEW_ST_DT=#{viewStDt}
    , VIEW_END_DT=#{viewEndDt}

        , FILE_GRP_SEQ=#{fileGrpSeq}

    , CONTENTS=#{contents}
WHERE
    STORY_SEQ=#{seq}
